        }
      ];
      setArticles(defaults);
      localStorage.setItem('maple_motion_articles', JSON.stringify(defaults));
    }
  }, []);

  const handleSave = () => {
    if (!newArticle.title || !newArticle.content || !newArticle.author) {
      toast.error("Please fill in all fields");
      return;
    }

    const article: Article = {
      id: Date.now().toString(),
      title: newArticle.title,
      content: newArticle.content,
      author: newArticle.author,
      date: new Date().toLocaleDateString()
    };

    const updatedArticles = [article, ...articles];
    setArticles(updatedArticles);
    localStorage.setItem('maple_motion_articles', JSON.stringify(updatedArticles));

    setNewArticle({ title: '', content: '', author: '' });
    setIsOpen(false);
    toast.success("Article published successfully!");
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />

      <div className="bg-slate-900 py-20 text-center text-white">
        <h1 className="text-4xl font-bold mb-4">News & Articles</h1>
        <p className="text-xl text-slate-300 max-w-2xl mx-auto px-4">
          Latest updates, transportation tips, and company news.
        </p>
      </div>

      <section className="py-16 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center mb-8">
          <h2 className="text-2xl font-bold text-slate-900">Recent Posts</h2>
          <Dialog open={isOpen} onOpenChange={setIsOpen}>
            <DialogTrigger asChild>
              <Button className="bg-red-600 hover:bg-red-700">
                <Plus className="mr-2 h-4 w-4" /> Write Article
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[600px]">
              <DialogHeader>
                <DialogTitle>Write a New Article</DialogTitle>
              </DialogHeader>
              <div className="space-y-4 py-4">
                <div className="space-y-2">
                  <Label htmlFor="title">Title</Label>
                  <Input 
                    id="title" 
                    value={newArticle.title}
                    onChange={(e) => setNewArticle({...newArticle, title: e.target.value})}
                    placeholder="Article Title"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="author">Author</Label>
                  <Input 
                    id="author" 
                    value={newArticle.author}
                    onChange={(e) => setNewArticle({...newArticle, author: e.target.value})}
                    placeholder="Your Name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="content">Content</Label>
                  <Textarea 
                    id="content" 
                    value={newArticle.content}
                    onChange={(e) => setNewArticle({...newArticle, content: e.target.value})}
                    placeholder="Write your article content here..."
                    className="min-h-[200px]"
                  />
                </div>
                <Button onClick={handleSave} className="w-full bg-red-600 hover:bg-red-700">
                  Publish Article
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>

        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {articles.map((article) => (
            <Card key={article.id} className="hover:shadow-lg transition-shadow">
              <CardHeader>
                <CardTitle className="line-clamp-2">{article.title}</CardTitle>
                <CardDescription className="flex items-center space-x-4 mt-2">
                  <span className="flex items-center text-xs"><Calendar className="mr-1 h-3 w-3" /> {article.date}</span>
                  <span className="flex items-center text-xs"><User className="mr-1 h-3 w-3" /> {article.author}</span>
                </CardDescription>
              </CardHeader>
              <CardContent>
                <p className="text-slate-600 line-clamp-4 text-sm">
                  {article.content}
                </p>
                <Button variant="link" className="px-0 text-red-600 mt-4">
                  Read More
                </Button>
              </CardContent>
            </Card>
          ))}
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Articles;
</content>
