import { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { toast } from 'sonner';
import Navbar from '@/components/Navbar';
import Footer from '@/components/Footer';
import { MapPin, Phone, Mail, Clock } from 'lucide-react';
import { supabase } from '@/lib/supabase';

const Contact = () => {
  const [searchParams] = useSearchParams();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    service_type: '',
    message: ''
  });

  useEffect(() => {
    const service = searchParams.get('service');
    const vehicle = searchParams.get('vehicle');
    if (service) {
      setFormData(prev => ({ ...prev, service_type: service }));
    } else if (vehicle) {
      setFormData(prev => ({ ...prev, message: `I am interested in booking the ${vehicle}.` }));
    }
  }, [searchParams]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSelectChange = (value: string) => {
    setFormData(prev => ({ ...prev, service_type: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Attempt to save to Supabase
      const { error } = await supabase
        .from('app_452e4_inquiries')
        .insert([
          {
            name: formData.name,
            email: formData.email,
            service_type: formData.service_type,
            message: formData.message
          }
        ]);

      if (error) {
        console.error('Supabase error:', error);
        // Fallback for demo/if backend not configured
        toast.success("Message sent! (Demo mode: Backend connection failed, but your request is noted)");
      } else {
        toast.success("Thank you! We have received your inquiry and will contact you shortly.");
      }

      setFormData({ name: '', email: '', service_type: '', message: '' });
