import { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { toast } from 'sonner';
import Navbar from '@/components/Navbar';
import Footer from '@/components/Footer';
import { MapPin, Phone, Mail, Clock } from 'lucide-react';
import { supabase } from '@/lib/supabase';

const Contact = () => {
  const [searchParams] = useSearchParams();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    service_type: '',
    message: ''
  });

  useEffect(() => {
    const service = searchParams.get('service');
    const vehicle = searchParams.get('vehicle');
    if (service) {
      setFormData(prev => ({ ...prev, service_type: service }));
    } else if (vehicle) {
      setFormData(prev => ({ ...prev, message: `I am interested in booking the ${vehicle}.` }));
    }
  }, [searchParams]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSelectChange = (value: string) => {
    setFormData(prev => ({ ...prev, service_type: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Attempt to save to Supabase
      const { error } = await supabase
        .from('app_452e4_inquiries')
        .insert([
          {
            name: formData.name,
            email: formData.email,
            service_type: formData.service_type,
            message: formData.message
          }
        ]);

      if (error) {
        console.error('Supabase error:', error);
        // Fallback for demo/if backend not configured
        toast.success("Message sent! (Demo mode: Backend connection failed, but your request is noted)");
      } else {
        toast.success("Thank you! We have received your inquiry and will contact you shortly.");
      }

      setFormData({ name: '', email: '', service_type: '', message: '' });
    } catch (err) {
      console.error('Submission error:', err);
      toast.success("Message sent! (Demo mode)");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />

      <div className="bg-slate-900 py-20 text-center text-white">
        <h1 className="text-4xl font-bold mb-4">Contact Us</h1>
        <p className="text-xl text-slate-300 max-w-2xl mx-auto px-4">
          Get a quote or ask a question. We're here to help.
        </p>
      </div>

      <section className="py-16 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Contact Information */}
          <div className="space-y-8">
            <div>
              <h2 className="text-2xl font-bold text-slate-900 mb-6">Get in Touch</h2>
              <p className="text-slate-600 mb-8">
                Fill out the form and our team will get back to you within 24 hours. For urgent inquiries, please call our dispatch line.
              </p>
            </div>

            <div className="space-y-6">
              <div className="flex items-start space-x-4">
                <div className="p-3 bg-red-100 rounded-lg">
                  <MapPin className="h-6 w-6 text-red-600" />
                </div>
                <div>
                  <h3 className="font-bold text-slate-900">Our Office</h3>
                  <p className="text-slate-600">Rideau Ottawa</p>
                </div>
              </div>

              <div className="flex items-start space-x-4">
                <div className="p-3 bg-red-100 rounded-lg">
                  <Phone className="h-6 w-6 text-red-600" />
                </div>
                <div>
                  <h3 className="font-bold text-slate-900">Phone</h3>
                  <p className="text-slate-600">Main: (343) 573-1820</p>
                  <p className="text-slate-600">Dispatch: (613) 555-0124</p>
                </div>
              </div>

              <div className="flex items-start space-x-4">
                <div className="p-3 bg-red-100 rounded-lg">
                  <Mail className="h-6 w-6 text-red-600" />
                </div>
                <div>
                  <h3 className="font-bold text-slate-900">Email</h3>
                  <p className="text-slate-600">contact@maplemotion.ca</p>
                </div>
              </div>

              <div className="flex items-start space-x-4">
                <div className="p-3 bg-red-100 rounded-lg">
                  <Clock className="h-6 w-6 text-red-600" />
                </div>
                <div>
                  <h3 className="font-bold text-slate-900">Hours</h3>
                  <p className="text-slate-600">Mon-Fri: 8:00 AM - 6:00 PM</p>
                  <p className="text-slate-600">Sat-Sun: 9:00 AM - 4:00 PM</p>
                </div>
              </div>
            </div>

            {/* Map Placeholder */}
            <div className="w-full h-64 bg-slate-100 rounded-xl overflow-hidden border border-slate-200 relative">
              <div className="absolute inset-0 flex items-center justify-center text-slate-400">
                <div className="text-center">
                  <MapPin className="h-12 w-12 mx-auto mb-2 opacity-50" />
                  <p>Map Integration Placeholder</p>
                  <p className="text-sm">(Google Maps API would go here)</p>
                </div>
              </div>
            </div>
          </div>

          {/* Contact Form */}
          <div className="bg-white p-8 rounded-2xl shadow-lg border border-slate-100">
            <h3 className="text-xl font-bold text-slate-900 mb-6">Send us a Message</h3>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="name">Full Name</Label>
                <Input 
                  id="name" 
                  name="name" 
                  value={formData.name} 
                  onChange={handleChange} 
                  placeholder="John Doe" 
                  required 
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">Email Address</Label>
                <Input 
                  id="email" 
                  name="email" 
                  type="email" 
                  value={formData.email} 
                  onChange={handleChange} 
                  placeholder="john@example.com" 
                  required 
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="service_type">Service Type</Label>
                <Select onValueChange={handleSelectChange} value={formData.service_type}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select a service" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="School Transportation">School Transportation</SelectItem>
                    <SelectItem value="Corporate Shuttles">Corporate Shuttles</SelectItem>
                    <SelectItem value="Diplomatic & VIP">Diplomatic & VIP</SelectItem>
                    <SelectItem value="Wedding Transfers">Wedding Transfers</SelectItem>
                    <SelectItem value="Airport & City Tours">Airport & City Tours</SelectItem>
                    <SelectItem value="Other">Other</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="message">Message</Label>
                <Textarea 
                  id="message" 
                  name="message" 
                  value={formData.message} 
                  onChange={handleChange} 
                  placeholder="Tell us about your trip details (date, destination, number of passengers)..." 
                  className="min-h-[150px]" 
                  required 
                />
              </div>

              <Button type="submit" className="w-full bg-red-600 hover:bg-red-700" disabled={isSubmitting}>
                {isSubmitting ? 'Sending...' : 'Send Message'}
              </Button>
            </form>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Contact;
</content>
